me="certificate" value="Work Note" \r\nameonclick="document.RichTextLetter.AddLetterhead.click();\r\n 	doHtml(\'<p>\'+doDate()+\'<p>This is to certify that I have today examined <p>\');\r\nameonclickdocument.RichTextLetter.AddLabel.click();\r\nameonclickdocumentdoHtml(\'In my opinion, \'+document.getElementById(\'he_she\').value+\' will be unfit for \'+document.getElementById(\'his_her\').value+\' normal work from today to * inclusive.\');\r\normaldocument.RichTextLetter.Closing.click();">\r\n<br>\r\n\r\n<input type="button" class="butn" name="consult" value="Consult Letter" \r\nameonclick="  var ref=document.getElementById(\'referral_name\').value.toString(); var mySplitResult = ref.split(\',\');\r\nameonclickvar gender=document.getElementById(\'sex\').value; if (gender==\'M\'){gender=\'male\';}; if (gender==\'F\'){gender=\'female\';};\r\nameonclickvarvar years=document.getElementById(\'ageComplex\').value; if (years==\'\'){years=document.getElementById(\'age\').value + \'yo\';};\r\nameonclickvarvardocument.RichTextLetter.AddLetterhead.click();\r\nameonclickvarvardocumentdoHtml(\'<p>\'+doDate()+\'<p>\');\r\nameonclickvarvardocumentdoHtmldocument.RichTextLetter.AddReferral.click();\r\nameonclickvarvardocumentdoHtmldocumentdoHtml(\'<p>RE:&nbsp\');\r\nbspdocument.RichTextLetter.AddLabel.click();\r\nbspdocumentdoHtml(\'<p>Dear Dr. \'+mySplitResult[0]+\'<p>Thank you for asking me to see this \'+years+ \' \' +gender);\r\nbspdocumentdoHtmldocument.RichTextLetter.Closing.click(); ">\r\n<br>\r\n-->\r\n<input type="button" class="butn" name="AddReferral" id="AddReferral" value="Referring Block" \r\nameonclick="doHtml(document.getElementById(\'referral_name\').value+\'<br>\'+ document.getElementById(\'referral_address\').value +\'<br>CANADA<br> Tel: \'+ document.getElementById(\'referral_phone\').value+\'<br>Fax:  \'+document.getElementById(\'referral_fax\').value);">\r\n\r\n<br>\r\n\r\n<input type="button" class="butn" name="AddLabel" id="AddLabel" value="Patient Block" \r\nameonclick="doHtml(document.getElementById(\'label\').value);">\r\n\r\n<br>\r\n\r\n<br>\r\n<input type="button"  class="butn" name="MedicalHistory" value="Recent History" width=30\r\nameonclick="var hist=parseText(document.getElementById(\'medical_history\').value); doHtml(hist);">\r\n<br>\r\n<input type="button"  class="butn" name="AddMedicalHistory" value="Full History" width=30\r\nameonclick="doHtml(document.getElementById(\'medical_history\').value); ">\r\n<br>\r\n<input type="button" class="butn" name="RecentMedications" id="RecentMedications" value="Recent Prescriptions"\r\nameonclick="doHtml(document.getElementById(\'recent_rx\').value);">\r\n<br>\r\n<input type="button" class="butn" name="Medlist" id="Medlist" value="Medication List"\r\nameonclick="doHtml(document.getElementById(\'druglist_trade\').value);">\r\n<br>\r\n<input type="button" class="butn" name="Allergies" id="Allergies" value="Meds & Allergies"\r\nameonclick="var allergy=document.getElementById(\'allergies_des\').value; if (allergy.length>0){allergy=\'<br>Allergies: \'+allergy};doHtml(\'Medications: \'+document.getElementById(\'druglist_line\').value+allergy);">\r\n<br>\r\n<input type="button" class="butn" name="OtherMedicationsHistory" value="Family History"\r\nameonclick="var hist=parseText(document.getElementById(\'other_medications_history\').value); doHtml(hist);">\r\n<br>\r\n<input type="button" class="butn" name="AddOtherMedicationsHistory" value="Full Family Hx"\r\nameonclick="doHtml(document.getElementById(\'other_medications_history\').value); ">\r\n\r\n<br>\r\n<input type="button" class="butn" name="AddSocialFamilyHistory" value="Social History" \r\nameonclick="var hist=parseText(document.getElementById(\'social_family_history\').value); doHtml(hist);">\r\n<br>\r\n<input type="button" class="butn" name="AddReminders" value="Reminders"\r\nameonclick="var hist=parseText(document.getElementById(\'reminders\').value); doHtml(hist);">\r\n<br>\r\n<input type="button" class="butn" name="AddOngoingConcerns" value="Ongoing Concerns"\r\nameonclick="var hist=parseText(document.getElementById(\'ongoingconcerns\').value); doHtml(hist);">\r\n<br>\r\n<br>\r\n<input type="button" class="butn" name="Patient" value="Patient Name"\r\nameonclick=" doHtml(document.getElementById(\'first_last_name\').value);">\r\n\r\n<br>\r\n<input type="button" class="butn" name="PatientAge" value="Patient Age"\r\nameonclick="var hist=document.getElementById(\'ageComplex\').value; if (hist==\'\'){hist=document.getElementById(\'age\').value;}; doHtml(hist);">\r\n<br>\r\n<input type="button" class="butn" name="label" value="Patient Label"\r\nameonclick="var hist=document.getElementById(\'label\').value; doHtml(hist);">\r\n\r\n\r\n<br>\r\n<input type="button" class="butn" name="PatientSex" value="Patient Gender"\r\nameonclick="doHtml(document.getElementById(\'sex\').value);">\r\n<br>\r\n<br>\r\n<input type="button" class="butn" name="Closing" value="Closing Salutation" \r\nameonclick=" doHtml(\'<p>Yours Sincerely<p>&nbsp;<p>\'+ document.getElementById(\'provider_name_first_init\').value+\', MD\');">\r\n \r\n<br>\r\n<input type="button" class="butn" name="User" value="Current User"\r\nameonclick="var hist=document.getElementById(\'current_user\').value; doHtml(hist);">\r\n \r\n<br>\r\n<input type="button" class="butn" name="Doctor" value="Attending Doctor"\r\nameonclick="var hist=document.getElementById(\'doctor\').value; doHtml(hist);">\r\n<br>\r\n<br>\r\n\r\n\r\n<br>\r\n</div>\r\n\r\n\r\n<div class="DoNotPrint" >\r\n<input onclick="viewsource(this.checked)" type="checkbox">\r\nHTML Source\r\n<input onclick="usecss(this.checked)" type="checkbox">\r\nUse CSS\r\nUse<table><tr><td>\r\nHTML Subject: <input name="subject" id="subject" size="40" type="text">\r\nHTML <input value="Submit" name="SubmitButton" type="submit" onclick="needToConfirm=false;document.getElementById(\'Letter\').value=editControlContents(\'edit\');  document.RichTextLetter.submit()">\r\name <input value="Reset" name="ResetButton" type="reset">\r\needToConfirm <input value="Print" name="PrintButton" type="button" onclick="document.getElementById(\'edit\').contentWindow.print();">\r\needToConfirm <input value="Print & Save" name="PrintSaveButton" type="button" onclick="document.getElementById(\'edit\').contentWindow.print();needToConfirm=false;document.getElementById(\'Letter\').value=editControlContents(\'edit\');  setTimeout(\'document.RichTextLetter.submit()\',1000);">\r\needToConfirm </td></tr></table>\r\n </div>\r\n </form>\r\n\r\n</body></html>' where fid=1 and form_name='letter';
